package com.syntax.class04;

public class MoreExamples {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		boolean mothersDay= true;
		if (mothersDay) { // In this case you dont neet to write == true or false
			System.out.println("Happy Mother's day to alls Moms");
		} else {
			System.out.println("Hello");
		}
		boolean rain=true;
		if (rain) {
			System.out.println("I will take an umbrella");
		} else {
			System.out.println("we don't need umbrella");
		}
		boolean breakTime=true;
		if (breakTime) {
			System.out.println("Let's take a break");
		} else {
			System.out.println("we need to still study");
		}
	}

}
