package com.syntax.reviewclass02;

public class OperatorsDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int num1=10;
		int num2=10;
		System.out.println(num1+num2);
		System.out.println(num1>=num2); // num1>num2 then false or num1=num2 then true, false + true = true
		// whole numer: 0 to infinity. Negative infinity to positive infinity
		System.out.println(Long.MAX_VALUE); // Gives us the maximum range
		System.out.println(Long.MIN_VALUE); // Gives us the maximum range
		System.out.println(Byte.MAX_VALUE);
		System.out.println(Integer.MAX_VALUE);
		System.out.println(num1/num2);
		System.out.println(num1%num2);
		System.out.println(10/3);
		System.out.println(10%3);
		System.out.println(10.0/3);
		System.out.println((int)10.0/3);
		
	}

}
